import React, { useState, useEffect } from 'react';
import { getReviews, deleteReview, getRecipes, getUsers, createReview, updateReview } from '../api/api';
import './TableComponent.css';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingReview, setEditingReview] = useState(null);
  const [formData, setFormData] = useState({
    recipe_id: '',
    user_id: '',
    rating: '',
    comment: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [reviewsRes, recipesRes, usersRes] = await Promise.all([
        getReviews(),
        getRecipes(),
        getUsers()
      ]);
      setReviews(reviewsRes.data);
      setRecipes(recipesRes.data);
      setUsers(usersRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingReview) {
        await updateReview(editingReview.id, formData);
        alert('Review updated successfully');
      } else {
        await createReview(formData);
        alert('Review created successfully');
      }
      setShowForm(false);
      setEditingReview(null);
      setFormData({ recipe_id: '', user_id: '', rating: '', comment: '' });
      fetchData();
    } catch (error) {
      console.error('Error saving review:', error);
      alert('Failed to save review');
    }
  };

  const handleEdit = (review) => {
    setEditingReview(review);
    setFormData({
      recipe_id: review.recipe_id,
      user_id: review.user_id,
      rating: review.rating,
      comment: review.comment || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this review?')) {
      try {
        await deleteReview(id);
        setReviews(reviews.filter(r => r.id !== id));
        alert('Review deleted successfully');
      } catch (error) {
        console.error('Error deleting review:', error);
        alert('Failed to delete review');
      }
    }
  };

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="table-container">
      <div className="page-header">
        <h2>Reviews</h2>
        <button className="btn btn-primary" onClick={() => {
          setShowForm(true);
          setEditingReview(null);
          setFormData({ recipe_id: '', user_id: '', rating: '', comment: '' });
        }}>
          + Add Review
        </button>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="form-modal-content">
            <h3>{editingReview ? 'Edit Review' : 'Add Review'}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Recipe</label>
                <select
                  value={formData.recipe_id}
                  onChange={(e) => setFormData({ ...formData, recipe_id: e.target.value })}
                  required
                >
                  <option value="">Select Recipe</option>
                  {recipes.map(r => (
                    <option key={r.id} value={r.id}>{r.title}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>User</label>
                <select
                  value={formData.user_id}
                  onChange={(e) => setFormData({ ...formData, user_id: e.target.value })}
                  required
                >
                  <option value="">Select User</option>
                  {users.map(u => (
                    <option key={u.id} value={u.id}>{u.username}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Rating (1-5)</label>
                <input
                  type="number"
                  min="1"
                  max="5"
                  value={formData.rating}
                  onChange={(e) => setFormData({ ...formData, rating: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Comment</label>
                <textarea
                  value={formData.comment}
                  onChange={(e) => setFormData({ ...formData, comment: e.target.value })}
                  rows="4"
                />
              </div>
              <div className="form-actions">
                <button type="button" className="btn btn-cancel" onClick={() => {
                  setShowForm(false);
                  setEditingReview(null);
                }}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">Save</button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="table-wrapper">
        <table className="data-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Recipe</th>
              <th>User</th>
              <th>Rating</th>
              <th>Comment</th>
              <th>Created</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {reviews.map(review => (
              <tr key={review.id}>
                <td>{review.id}</td>
                <td>{review.recipe_title}</td>
                <td>{review.username}</td>
                <td>{'‚≠ê'.repeat(review.rating)}</td>
                <td>{review.comment || '-'}</td>
                <td>{new Date(review.created_at).toLocaleDateString()}</td>
                <td>
                  <button className="btn-icon" onClick={() => handleEdit(review)}>‚úèÔ∏è</button>
                  <button className="btn-icon" onClick={() => handleDelete(review.id)}>üóëÔ∏è</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Reviews;
